// ğŸ“ PROCESADOR DE COMANDOS
// Este nodo analiza el mensaje recibido y valida el comando

// Extraer el mensaje de texto del payload de WhatsApp
const incomingMessage = $json.entry?.[0]?.changes?.[0]?.value?.messages?.[0];

if (!incomingMessage || incomingMessage.type !== 'text') {
  return [{
    json: {
      isValid: false,
      error: 'No se recibiÃ³ un mensaje de texto vÃ¡lido',
      phoneNumber: 'unknown',
      messageId: 'unknown'
    }
  }];
}

// Extraer informaciÃ³n del mensaje
const messageText = incomingMessage.text.body;
const phoneNumber = incomingMessage.from;
const messageId = incomingMessage.id;
const contactName = $json.entry?.[0]?.changes?.[0]?.value?.contacts?.[0]?.profile?.name || 'Usuario';

// Convertir comando a mayÃºsculas y eliminar espacios
const command = messageText.trim().toUpperCase();

// Validar comandos permitidos
const validCommands = ['ON', 'OFF', 'BLINK', 'STATUS'];
const isValidCommand = validCommands.includes(command);

// ConfiguraciÃ³n del dispositivo Arduino IoT Cloud
const deviceConfig = {
  thingId: 'a5c7e315-765b-465c-8044-9d3c7f19490e',  // Device ID del PDF
  variableName: 'ledCommand',  // Variable configurada en el Thing
  deviceName: 'ESP8266_IoT_LED'
};

// Preparar respuesta
if (isValidCommand) {
  // Comando vÃ¡lido - preparar para enviar a Arduino IoT Cloud
  return [{
    json: {
      isValid: true,
      command: command,
      phoneNumber: phoneNumber,
      messageId: messageId,
      contactName: contactName,
      thingId: deviceConfig.thingId,
      variableName: deviceConfig.variableName,
      deviceName: deviceConfig.deviceName,
      apiUrl: `https://api2.arduino.cc/iot/v2/things/${deviceConfig.thingId}/properties/${deviceConfig.variableName}`,
      timestamp: new Date().toISOString()
    }
  }];
} else {
  // Comando invÃ¡lido - preparar mensaje de error
  return [{
    json: {
      isValid: false,
      command: command,
      phoneNumber: phoneNumber,
      messageId: messageId,
      contactName: contactName,
      errorMessage: `âŒ Comando no reconocido: "${messageText}"\n\n` +
                   `ğŸ“‹ Comandos disponibles:\n` +
                   `â€¢ *ON* - Encender LED\n` +
                   `â€¢ *OFF* - Apagar LED\n` +
                   `â€¢ *BLINK* - Modo parpadeo\n` +
                   `â€¢ *STATUS* - Ver estado actual\n\n` +
                   `EnvÃ­a uno de estos comandos para controlar el dispositivo.`,
      timestamp: new Date().toISOString()
    }
  }];
}

// ğŸ“Š FORMATEAR RESPUESTA DE ESTADO
// Procesa la respuesta de Arduino IoT Cloud y formatea el mensaje

const properties = $json.body || $json || [];
const phoneNumber = $node["Process WhatsApp Command"].json.phoneNumber;
const contactName = $node["Process WhatsApp Command"].json.contactName;

// Buscar las propiedades relevantes
let ledStatus = 'Desconocido';
let ledCommand = 'Desconocido';
let deviceOnline = false;
let lastUpdate = 'Nunca';

properties.forEach(prop => {
  if (prop.name === 'ledStatus') {
    ledStatus = prop.last_value || 'Desconocido';
  }
  if (prop.name === 'ledCommand') {
    ledCommand = prop.last_value || 'Desconocido';
  }
  if (prop.name === 'deviceOnline') {
    deviceOnline = prop.last_value === true;
  }
  if (prop.updated_at) {
    // Formatear fecha a hora local
    const date = new Date(prop.updated_at);
    lastUpdate = date.toLocaleString('es-ES', {
      timeZone: 'America/Mexico_City',
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  }
});

// Crear mensaje de estado formateado
const statusMessage = 
  `ğŸ“Š *Estado del Dispositivo ESP8266*\n\n` +
  `ğŸ‘¤ Hola ${contactName}!\n\n` +
  `ğŸ”Œ *ConexiÃ³n:* ${deviceOnline ? 'âœ… En lÃ­nea' : 'âŒ Desconectado'}\n` +
  `ğŸ’¡ *Estado LED:* ${ledStatus}\n` +
  `ğŸ“ *Ãšltimo comando:* ${ledCommand}\n` +
  `ğŸ• *Actualizado:* ${lastUpdate}\n\n` +
  `_EnvÃ­a ON, OFF o BLINK para controlar el LED_`;

return [{
  json: {
    success: true,
    message: statusMessage,
    phoneNumber: phoneNumber,
    ledStatus: ledStatus,
    deviceOnline: deviceOnline,
    lastUpdate: lastUpdate
  }
}];

// ğŸ¯ PROCESAR RESPUESTA DE COMANDO
// Analiza si el comando se ejecutÃ³ correctamente

const command = $node["Process WhatsApp Command"].json.command;
const phoneNumber = $node["Process WhatsApp Command"].json.phoneNumber;
const contactName = $node["Process WhatsApp Command"].json.contactName;
const deviceName = $node["Process WhatsApp Command"].json.deviceName;
const responseStatus = $json.statusCode || 'unknown';
const responseBody = $json.body;

// Verificar si la respuesta fue exitosa
const success = responseStatus >= 200 && responseStatus < 300;

if (success) {
  // Comando ejecutado exitosamente
  let actionDescription = '';

  switch(command) {
    case 'ON':
      actionDescription = 'ğŸ’¡ LED encendido';
      break;
    case 'OFF':
      actionDescription = 'ğŸ’¡ LED apagado';
      break;
    case 'BLINK':
      actionDescription = 'ğŸ’¡ LED en modo parpadeo';
      break;
  }

  const successMessage = 
    `âœ… *Comando ejecutado exitosamente*\n\n` +
    `ğŸ‘¤ ${contactName}\n` +
    `ğŸ“± Dispositivo: ${deviceName}\n` +
    `âš¡ Comando: ${command}\n` +
    `${actionDescription}\n\n` +
    `_El dispositivo ha recibido tu comando_`;

  return [{
    json: {
      success: true,
      message: successMessage,
      phoneNumber: phoneNumber,
      command: command,
      status: 'executed'
    }
  }];
} else {
  // Error al ejecutar el comando
  const errorMessage = 
    `âŒ *Error al ejecutar comando*\n\n` +
    `No se pudo enviar el comando "${command}" al dispositivo.\n\n` +
    `ğŸ“± Dispositivo: ${deviceName}\n` +
    `ğŸ”´ CÃ³digo de error: ${responseStatus}\n\n` +
    `Posibles causas:\n` +
    `â€¢ El dispositivo estÃ¡ desconectado\n` +
    `â€¢ Error de conexiÃ³n con Arduino IoT Cloud\n` +
    `â€¢ Token de API invÃ¡lido\n\n` +
    `_Intenta nuevamente en unos momentos_`;

  return [{
    json: {
      success: false,
      message: errorMessage,
      phoneNumber: phoneNumber,
      command: command,
      status: 'failed',
      error: responseBody?.error || 'Unknown error'
    }
  }];
}

// ğŸ“ PROCESAR REGISTRO DE DISPOSITIVO
// Registra cuando un ESP8266 se conecta

const ip = $json.body?.ip || 'unknown';
const deviceId = $json.body?.device_id || 'unknown';
const firmwareVersion = $json.body?.firmware || 'unknown';

console.log(`Dispositivo registrado: ${deviceId} desde IP ${ip}`);

return [{
  json: {
    success: true,
    message: "Device registration successful",
    deviceId: deviceId,
    ip: ip,
    firmwareVersion: firmwareVersion,
    timestamp: new Date().toISOString(),
    note: "Device registered in n8n workflow"
  }
}];
